import { useState } from "react";

const useInput = (initialValue, regex) => {

  const [value, setValue] = useState(initialValue);

  return {
    value,
    setValue,
    reset: () => setValue(""),
    bind: {
      value,
      onChange: e => {
        const { value } = e.target.value;

        if (value === '' || (regex && regex.test(value))) {
            setValue({value})
        }
      }
    }
  };
};

export default useInput;
