import { useLocation } from 'react-router-dom';
import { useQuery } from 'react-query';

import crudClient from 'api/crudClient';
import spotifyClient from 'api/spotifyClient';
import qs from 'qs';

const useToken = () => {
    const { data: user } = useQuery('spotifyUser', () => spotifyClient.getUser());

    const location = useLocation();

    const token = qs.parse(location.search, { ignoreQueryPrefix: true }).access_token;
    const oldToken = localStorage.getItem('jwt');

    if(!oldToken || oldToken !== token) {

        console.log(user)

        updateToken(token);
    }

    return token;
};

const updateToken = token => {
    console.log(user)
    //crudClient('members').partialUpdate(id, {token}).then(console.log).catch(console.log);
    localStorage.setItem('token', token);
}

export default useToken;
