import { get } from 'api/xhr';
import { useQuery } from 'react-query';

const useCurrentUser = () => {
    const { data: user, error, loading } = useQuery('currentUser', () => get('/auth/users/me'));
    return { user, error, loading };
};

export default useCurrentUser;
