import { get } from './xhr';
import crudClient from 'api/crudClient';

const partyAdapter = {
    ...crudClient('parties'),
    join: accessCode => {
        return get('/join-party/', {
            params: { accessCode }
        })
    }
};

export default partyAdapter;
