import { patch } from './xhr';
import crudClient from 'api/crudClient';

const partyAdapter = {
    ...crudClient('parties'),
    join: accessCode => {
        return patch('/joi/', {
            params: { accessCode }
        })
    }
};

export default partyAdapter;
