import { oauthURL } from 'config';

import crudClient from 'api/crudClient';
import useToken from 'hooks/useToken';

import Button from 'components/Button';
import React, { useEffect, useState } from 'react';

const Landing = () => {

    const token = useToken();
    const [userId, setUserId] = useState();

    useEffect(() => {
        fetch("https://api.spotify.com/v1/me", {
            headers: {
                'Authorization': `Bearer ${token}`
            }
        }).then(res =>  res.json()).then(user => user.id).then(setUserId);
    }, [token]); 

    useEffect(() => {

        const membersAdapter = crudClient('members');

        membersAdapter.single(userId)
            .then(console.log)
            .catch(error => {
                const { status } = error.response;

                if(status === 404) {
                    membersAdapter.create
                }
            })
    }, [userId]);

    return(
        <Button onClick={() => window.location = oauthURL}>Login with Spotify</Button>
    );
}

export default Landing;
