import useCurrentUser from 'hooks/useCurrentUser';
import partyAdapter from 'api/partyAdapter';

import LeaveParty from './LeaveParty';
import React from 'react';

const LeavePartyContainer = ({ partyId }) => {

    const { user } = useCurrentUser();

    const endParty = () => {
        partyAdapter.remove(partyId).then(() => {
            alert("The host has ended this party.");
            window.location = "/";
        }).catch(() => alert("Unable to delete party."));
    }

    const leaveParty = () => {
        partyAdapter.removeCurrentUser().then()
    }

    return(
        <>
            {user &&
                <LeaveParty isHost={user.host} endParty={endParty} leaveParty={leaveParty} />
            }   
        </>
    )

}

export default LeavePartyContainer;
