import JoinParty from './JoinParty';
import React from 'react';

const JoinPartyContainer = () => {

    const { value: accessCode, bind: bindAccessCode, reset: resetAccessCode } = useInput('');
    const [error, setError] = useState();

    const handleSubmit = event => {
        event.preventDefault();

        if(isValid(accessCode)) {
            alert(`Submitting Name ${accessCode}`);
            resetAccessCode();
        }
    }

    const isValid = accessCode => {
        if(accessCode.length === 6) {
            return true;
        } else {
            setError("Access code should be six digits long.");
            return false;
        }
    }
}

export default JoinPartyContainer;
